import React from 'react';
import ReactDOM, { render } from 'react-dom';
import './styles.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends React.Component {
  render(){  
    return(
        <div className="wrapper">
          <Clock/>
        </div>
      );
  }
}




class Clock extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      isPlay: false,
      minSessLeft: '25',
      secSessLeft: '00',
      minBreakLeft: '05',
      secBreakLeft: '00',
      breakLength: 5,
      sessionLength: 25,
      intervalId: -1,
      playingSessionTimer: true
    };
  }

  componentDidUpdate() {

  }


  setTime = () => {  
    const min = parseInt(this.state.minSessLeft);
    const sec = parseInt(this.state.secSessLeft);
    this.setState ({
      minSessLeft: sec==0?
                min - 1:
                min,
      secSessLeft: sec > 0? 
                sec - 1:
                59
    })
  };




  interval = () => {const intervalID = setInterval(this.setTime, 1000); return intervalID};

  handlePlay = () => {
    this.setState({
      isPlay: this.state.isPlay?false:true,
      intervalId: this.interval(),
    });
  }
  handlePause = () => {    
    this.setState({
      isPlay: this.state.isPlay?false:true,
      intervalId: clearInterval(this.state.intervalId)
    });
  }

  handleInitialize = () => {
    this.setState ({
      isPlay: false,
      minSessLeft: '25',
      secSessLeft: '00',
      breakLength: 5,
      sessionLength: 25,
      intervalId: clearInterval(this.state.intervalId)
    });    
  }


  handleBreakDecrement = () => {
    const {isPlay, minBreakLeft, breakLength} = this.state;
    if (breakLength > 1 && 
      !isPlay &&
      breakLength == minBreakLeft) {
      this.setState({
        breakLength: breakLength - 1,
        minBreakLeft: breakLength - 1,
        secBreakLeft: '00',
      })
    } else if (breakLength > 1 && 
      !isPlay &&
      breakLength != minBreakLeft) {
      this.setState({
        breakLength: minBreakLeft - 1,
        minBreakLeft: minBreakLeft - 1,
        secBreakLeft: '00',
      })
    }
  }

  handleBreakIncrement = () => {
    const {isPlay, minBreakLeft, breakLength} = this.state;
    if (breaksionLength < 5 && 
      !isPlay &&
      breakLength == minBreakLeft) {
      this.setState({
        breakLength: breakLength + 1,
        minBreakLeft: breakLength + 1,
        secBreakLeft: '00',
      })
    } else if (breakLength < 5 && 
      !isPlay &&
      breakLength != minBreakLeft) {
      this.setState({
        breakLength: minBreakLeft + 1,
        minBreakLeft: minBreakLeft + 1,
        secBreakLeft: '00',
      })
    }
  }

  handleSessionDecrement = () => {
    const {isPlay, minSessLeft, sessionLength} = this.state;
    if (sessionLength > 1 && 
      !isPlay &&
      sessionLength == minSessLeft) {
      this.setState({
        sessionLength: sessionLength - 1,
        minSessLeft: sessionLength - 1,
        secSessLeft: '00',
      })
    } else if (sessionLength > 1 && 
      !isPlay &&
      sessionLength != minSessLeft) {
      this.setState({
        sessionLength: minSessLeft - 1,
        minSessLeft: minSessLeft - 1,
        secSessLeft: '00',
      })
    }
  }

  handleSessionIncrement = () => {
    const {isPlay, minSessLeft, sessionLength} = this.state;
    if (sessionLength < 25 && 
      !isPlay &&
      sessionLength == minSessLeft) {
      this.setState({
        sessionLength: sessionLength + 1,
        minSessLeft: sessionLength + 1,
        secSessLeft: '00',
      })
    } else if (sessionLength < 25 && 
      !isPlay &&
      sessionLength != minSessLeft) {
      this.setState({
        sessionLength: minSessLeft + 1,
        minSessLeft: minSessLeft + 1,
        secSessLeft: '00',
      })
    }
  }

  handleReset = () => {
    this.handleInitialize()
  }

  render() {
    return (
      <div className="container"  style={{textAlign:"center"}}>     
        <div className="row">
          <div className="col col-sm-3"></div>
          <div className="col col-sm-3">
            <div id="break-label">Break length</div>
            <button id="break-decrement" onClick={this.handleBreakDecrement}>
              <i className="fas fa-arrow-circle-down" /> 
            </button>
            <div id="break-length">
              {
                this.state.breakLength
              }
            </div>
            <button id="break-increment" onClick={this.handleBreakIncrement}>
              <i className="fas fa-arrow-circle-up" /> 
            </button>
          </div>
          <div className="col col-sm-3">    
            <div id="session-label">Session length</div>        
            <button id="session-decrement" onClick={this.handleSessionDecrement}>
              <i className="fas fa-arrow-circle-down" />
            </button>
            <div id="session-length">
              {
                this.state.sessionLength
              }
            </div>
            <button id="session-increment" onClick={this.handleSessionIncrement}>
              <i className="fas fa-arrow-circle-up" /> 
            </button>
          </div>
          <div className="col col-sm-3"></div>
        </div> 
        <div className="row">
          <div className="col col-sm-4"></div>
          <div className="col col-sm-4 session-border">
            <div id="timer-label"><p style={{fontSize:"40pt"}}>Session</p></div>
            <div id="time-left">
              {
                playingSessionTimer?
                  this.state.minSessLeft + ':' + this.state.secSessLeft
                :
                  this.state.minBreakLeft + ':' + this.state.secBreakLeft
              }
            </div>
          </div>
          <div className="col col-sm-4"></div>
        </div>  
        <div className="row">
            <div className="col col-sm-12">
              {
                !this.state.isPlay?
                  <button id="start" onClick={this.handlePlay}>
                    <i className="fas fa-solid fa-play"></i>
                  </button>
                :
                  <button id="pause" onClick={this.handlePause}>
                    <i className="fas fa-solid fa-pause"></i>
                  </button>
              }
              <button id="reset" onClick={this.handleReset}>
                <i className="fas fa-solid fa-stop"></i>
              </button> 
            </div>
        </div>             

      </div>
    );
  }
}


ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
