import React from 'react';
import ReactDOM, { render } from 'react-dom';
import './styles.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const initState = {
  breakLength: 5,
  sessionLength: 25  
}

class App extends React.Component {
  render(){  
    return(
        <div className="wrapper">
          <Clock/>
        </div>
      );
  }
}



class Clock extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      isStart: false,
      sessionLeft: "25:00",
    };
  }

  handleInitialize = () => {

  }

  handleStartStop = () => {
    this.setState({
      isStart: this.state.isStart?false:true,
    })
  }

  render() {
    return (
      <div className="container"  style={{textAlign:"center"}}>
        <div className="row" style={{fontSize:"30pt", fontWeight:"bold"}}>
          <div id="break-label" className="col col-6-sm">Break length</div>
          <div id="session-label" className="col col-6-sm">Session length</div>  
        </div>
        <div className="row">
          <div className="col col-sm-6">
            <button id="break-decrement">
              <i className="fas fa-arrow-circle-down" /> 
            </button>
            <div id="break-length">
            </div>
            <button id="break-increment">
              <i className="fas fa-arrow-circle-up" /> 
            </button>
          </div>
          <div className="col col-sm-6">          
            <button id="session-decrement">
              <i className="fas fa-arrow-circle-down" />
            </button>
            <div id="session-length"></div>
            <button id="session-increment">
              <i className="fas fa-arrow-circle-up" /> 
            </button>
          </div>
        </div> 
        <div className="row">
          <div className="col col-sm-4"></div>
          <div className="col col-sm-4 session-border">
            <div id="timer-label"><p style={{fontSize:"40pt"}}>Session</p></div>
            <div id="time-left">
              <input type="time" valueAsNumber={this.state.sessionLeft}></input>
            </div>
          </div>
          <div className="col col-sm-4"></div>
        </div>  
        <div className="row">
            <div className="col col-sm-12">
              <button id="start_stop" onClick={this.handleStartStop}>
                {
                  !this.state.isStart?<i className="fas fa-solid fa-play"></i>:<i className="fas fa-solid fa-pause"></i>
                }
              </button>
              <button id="reset" onClick={this.handleInitialize}>
                <i className="fas fa-solid fa-stop"></i>
              </button> 
            </div>
        </div>             

      </div>
    );
  }
}


ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
