import React from 'react';
import ReactDOM, { render } from 'react-dom';
import './styles.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends React.Component {
  render(){  
    return(
        <div className="wrapper">
          <Clock/>
        </div>
      );
  }
}



class Clock extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      isStart: false,
      minLeft: '25',
      secLeft: '00',
      breakLength: 5,
      sessionLength: 25
    };
  }

  handleInitialize = () => {
    this.setState ({
      isStart: false,
      minLeft: '25',
      secLeft: '00',
      breakLength: 5,
      sessionLength: 25
    });
  }


  handleBreakDecrement = () => {
    if (this.state.breakLength > 0) {
      this.setState({
        breakLength: this.state.breakLength - 1
      })
    }
  }

  handleBreakIncrement = () => {
    if (this.state.breakLength < 5) {
      this.setState({
        breakLength: this.state.breakLength + 1
      })
    }
  }

  handleSessionDecrement = () => {
    if (this.state.sessionLength > 10) {
      this.setState({
        sessionLength: this.state.sessionLength - 1
      })
    }
  }

  handleSessionIncrement = () => {
    if (this.state.sessionLength < 25) {
      this.setState({
        sessionLength: this.state.sessionLength + 1
      })
    }
  }

  handlePlay = () => {
    let min = parseInt(this.state.minLeft);
    let sec = parseInt(this.state.secLeft);
    if (this.state.isStart) {
      setInterval(
        this.setState ({
          minLeft: sec==1?
                    min - 1:
                    min,
          secLeft: sec > 0? 
                   sec - 1:
                   59
        }),
        1000
      )
    }

    this.setState({
      isStart: this.state.isStart?false:true,
    })
  }

  handlePause = () => {
    this.setState({
      isStart: this.state.isStart?false:true,
    })    
  }

  render() {
    return (
      <div className="container"  style={{textAlign:"center"}}>     
        <div className="row">
          <div className="col col-sm-3"></div>
          <div className="col col-sm-3">
            <div id="break-label">Break length</div>
            <button id="break-decrement" onClick={this.handleBreakDecrement}>
              <i className="fas fa-arrow-circle-down" /> 
            </button>
            <div id="break-length">
              {
                this.state.breakLength
              }
            </div>
            <button id="break-increment" onClick={this.handleBreakIncrement}>
              <i className="fas fa-arrow-circle-up" /> 
            </button>
          </div>
          <div className="col col-sm-3">    
            <div id="session-label">Session length</div>        
            <button id="session-decrement" onClick={this.handleSessionDecrement}>
              <i className="fas fa-arrow-circle-down" />
            </button>
            <div id="session-length">
              {
                this.state.sessionLength
              }
            </div>
            <button id="session-increment" onClick={this.handleSessionIncrement}>
              <i className="fas fa-arrow-circle-up" /> 
            </button>
          </div>
          <div className="col col-sm-3"></div>
        </div> 
        <div className="row">
          <div className="col col-sm-4"></div>
          <div className="col col-sm-4 session-border">
            <div id="timer-label"><p style={{fontSize:"40pt"}}>Session</p></div>
            <div id="time-left">
              {
                this.state.minLeft + ':' + this.state.secLeft
              }
            </div>
          </div>
          <div className="col col-sm-4"></div>
        </div>  
        <div className="row">
            <div className="col col-sm-12">
              {
                !this.state.isStart?
                  <button id="start_stop" onClick={this.handlePlay}>
                    <i className="fas fa-solid fa-play"></i>
                  </button>
                :
                  <button id="start_stop" onClick={this.handlePause}>
                    <i className="fas fa-solid fa-pause"></i>
                  </button>
              }
              <button id="reset" onClick={this.handleInitialize}>
                <i className="fas fa-solid fa-stop"></i>
              </button> 
            </div>
        </div>             

      </div>
    );
  }
}


ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
